            </ListItemButton>
          </ListItem>
          <ListItem disablePadding>
            <ListItemButton onClick={() => setIsMobileMenuOpen(false)}>
              <Link href="/my-learning" style={{ textDecoration: "none", color: "inherit", width: "100%" }}>
