              </Link>
            </ListItemButton>
          </ListItem>
          <ListItem disablePadding>
            <ListItemButton onClick={() => setIsMobileMenuOpen(false)}>
              <Link href="/instructor" style={{ textDecoration: "none", color: "inherit", width: "100%" }}>
                <ListItemText primary="Teach" />
              </Link>
            </ListItemButton>
          </ListItem>

          <Divider sx={{ my: 1, borderColor: "rgba(255, 255, 255, 0.2)" }} />

          <ListItem sx={{ px: 2, py: 1 }}>
            <Avatar
              src={user.avatar || "/placeholder.svg"}
              alt={user.name}
              sx={{
                width: 32,
                height: 32,
                mr: 2,
                border: "1px solid rgba(255, 255, 255, 0.2)",
                bgcolor: "#3b82f6",
              }}
            >
              {user.name
                .split(" ")
                .map((n) => n[0])
                .join("")}
            </Avatar>
            <Box>
              <Typography variant="body2" fontWeight="medium">
                {user.name}
              </Typography>
              <Typography variant="caption" sx={{ color: "rgba(255, 255, 255, 0.7)" }}>
                {user.email}
              </Typography>
            </Box>
          </ListItem>

          <ListItem disablePadding>
            <ListItemButton onClick={() => setIsMobileMenuOpen(false)}>
              <ListItemIcon>
                <PersonIcon sx={{ color: "white" }} />
              </ListItemIcon>
              <Link href="/profile" style={{ textDecoration: "none", color: "inherit", width: "100%" }}>
                <ListItemText primary="Profile" />
              </Link>
            </ListItemButton>
          </ListItem>

          <ListItem disablePadding>
            <ListItemButton onClick={() => setIsMobileMenuOpen(false)}>
              <ListItemIcon>
                <SettingsIcon sx={{ color: "white" }} />
              </ListItemIcon>
              <Link href="/settings" style={{ textDecoration: "none", color: "inherit", width: "100%" }}>
                <ListItemText primary="Settings" />
              </Link>
            </ListItemButton>
          </ListItem>

          <ListItem disablePadding>
            <ListItemButton onClick={handleLogout}>
