                <LogoutIcon sx={{ color: "#fca5a5" }} />
              </ListItemIcon>
              <ListItemText primary="Logout" sx={{ color: "#fca5a5" }} />
            </ListItemButton>
          </ListItem>
        </List>
      </Drawer>
    </>
  )
}

export default Navbar

